import React, { createContext, useState, useRef, useEffect } from "react";
import { sendMsgToAI } from "./Gemini";

export const ContextApp = createContext();

function AppContext({ children }) {
  const [showSlide, setShowSlide] = useState(false);
  const [Mobile, setMobile] = useState(false);
  const [loading, setLoading] = useState(false); // This state tracks loading
  const [chatValue, setChatValue] = useState("");
  const [message, setMessage] = useState([]);
  const msgEnd = useRef(null);

  // This state is for the mic language
  const [language, setLanguage] = useState('en-US'); 

  useEffect(() => {
    // Scroll to the end of messages, including when loader appears
    msgEnd.current.scrollIntoView({ behavior: "smooth" });
  }, [message, loading]); // Added loading as a dependency

  const handleSend = async () => {
    if (chatValue.trim() === "") return; 

    const userMessage = { text: chatValue, isBot: false };
    setMessage((prev) => [...prev, userMessage]); 
    setChatValue(""); 
    setLoading(true); // Set loading to true right before API call

    try {
      const res = await sendMsgToAI(chatValue);
      const botMessage = { text: res, isBot: true };
      setMessage((prev) => [...prev, botMessage]); 
    } catch (error) {
      console.error("Error sending message:", error);
      const errorMessage = {
        text: "Sorry, I ran into an error. Please try again.",
        isBot: true,
      };
      setMessage((prev) => [...prev, errorMessage]);
    } finally {
      setLoading(false); // Set loading to false after response or error
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault(); 
      handleSend();
    }
  };

  const contextValue = {
    showSlide,
    setShowSlide,
    Mobile,
    setMobile,
    loading,       // <-- ADDED THIS
    setLoading,    // <-- ADDED THIS
    chatValue,
    setChatValue,
    message,
    setMessage,
    msgEnd,
    handleSend,
    handleKeyPress,
    language,
    setLanguage,
  };

  return (
    <ContextApp.Provider value={contextValue}>{children}</ContextApp.Provider>
  );
}

export default AppContext;
