import React, { createContext, useState, useRef, useEffect } from "react";
import { sendMsgToAI } from "./Gemini"; // This is your new streaming function

export const ContextApp = createContext();

function AppContext({ children }) {
  const [showSlide, setShowSlide] = useState(false);
  const [Mobile, setMobile] = useState(false);
  const [loading, setLoading] = useState(false);
  const [chatValue, setChatValue] = useState("");
  const [message, setMessage] = useState([]);
  const msgEnd = useRef(null);
  const [language, setLanguage] = useState('en-US');

  useEffect(() => {
    msgEnd.current.scrollIntoView({ behavior: "smooth" });
  }, [message, loading]);

  // --- THIS IS THE CORRECTED handleSend FUNCTION ---
  const handleSend = async () => {
    if (chatValue.trim() === "") return;

    const userMessage = { text: chatValue, isBot: false };
    // Create an empty bot message template. This is the key!
    const botMessageTemplate = { text: "", isBot: true }; 
    const messageToSend = chatValue; // Store message before clearing
    
    // 1. Add user message, THEN the empty bot message
    setMessage((prev) => [...prev, userMessage, botMessageTemplate]);
    
    setChatValue(""); 
    
    // Set loading to true (this is now used by Chat.js to decide to stream)
    setLoading(true); 

    try {
      // 2. Define the onChunk callback function
      // This function will be called by Gemini.js with new text chunks
      const onChunk = (chunk) => {
        // We update the *last* message in the state (the empty bot one)
        setMessage((prev) => {
          const lastMsg = prev[prev.length - 1];
          const allButLast = prev.slice(0, -1);
          
          // Append the new chunk to the last message's text
          return [
            ...allButLast,
            { ...lastMsg, text: lastMsg.text + chunk }
          ];
        });
      };

      // 3. Call the streaming function WITH the callback
      // This is the line that fixes your error
      await sendMsgToAI(messageToSend, onChunk);

    } catch (error) {
      console.error("Error sending message:", error);
      // The streaming function will send its own error chunk
    } finally {
      setLoading(false); // Stop loading state when stream is done
    }
  };
  // --- END OF CORRECTION ---

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault(); 
      handleSend();
    }
  };

  const contextValue = {
    showSlide,
    setShowSlide,
    Mobile,
    setMobile,
    loading,
    setLoading,
    chatValue,
    setChatValue,
    message,
    setMessage,
    msgEnd,
    handleSend,
    handleKeyPress,
    language,
    setLanguage,
  };

  return (
    <ContextApp.Provider value={contextValue}>{children}</ContextApp.Provider>
  );
}

export default AppContext;
