import React, { createContext, useState, useRef, useEffect, useCallback } from "react";
import { sendMsgToAI } from "./Gemini"; // This is now the STREAMING function

export const ContextApp = createContext();

function AppContext({ children }) {
  const [showSlide, setShowSlide] = useState(false);
  const [Mobile, setMobile] = useState(false);
  const [loading, setLoading] = useState(false); // We still use this, but only briefly
  const [chatValue, setChatValue] = useState("");
  const [message, setMessage] = useState([]);
  const msgEnd = useRef(null);
  const [language, setLanguage] = useState('en-US');

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [message]); // Removed loading, as streaming updates message directly

  // --- RE-ENABLING STREAMING handleSend ---
  const handleSend = useCallback(async (text) => {
    const promptText = text || chatValue;
    if (promptText.trim() === "") return;

    const userMessage = { text: promptText, isBot: false };
    // Create an empty bot message template. This is the key!
    const botMessageTemplate = { text: "", isBot: true }; 
    
    if (!text) {
      setChatValue(""); 
    }
    
    // We set loading to true just to signal the process has started
    setLoading(true); 

    // This is the key: We use the functional update form of setMessage
    setMessage((prev) => {
      const newHistory = [...prev, userMessage, botMessageTemplate]; // Add user msg AND empty bot msg
      const historyForApi = [...prev, userMessage]; // History for API doesn't include the empty bot msg

      // Define the onChunk callback function
      const onChunk = (chunk) => {
        setMessage((prevChunk) => {
          const lastMsg = prevChunk[prevChunk.length - 1];
          const allButLast = prevChunk.slice(0, -1);
          
          return [
            ...allButLast,
            { ...lastMsg, text: lastMsg.text + chunk }
          ];
        });
      };

      // Call the API from *inside* the updater to avoid race conditions
      (async () => {
        try {
          // Send the history *before* the empty bot message was added
          await sendMsgToAI(historyForApi, onChunk);
        } catch (error) {
          console.error("Error sending message:", error);
          // The stream itself will handle error messages
        } finally {
          setLoading(false); // Signal that the stream is finished
        }
      })();
      
      return newHistory; // Return the new state (with empty bot msg)
    });

  }, [chatValue]); 
  // --- END OF STREAMING handleSend ---

  const handleKeyPress = useCallback((e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault(); 
      handleSend(); 
    }
  }, [handleSend]);
  
  const handleQuery = useCallback(async (e) => {
    const text = e.target.innerText;
    if (!text) return;

    // This also needs to use the streaming logic
    setLoading(true);
    setMessage((prev) => {
      const userMessage = { text, isBot: false };
      const botMessageTemplate = { text: "", isBot: true };
      const newHistory = [...prev, userMessage, botMessageTemplate];
      const historyForApi = [...prev, userMessage];

      const onChunk = (chunk) => {
        setMessage((prevChunk) => {
          const lastMsg = prevChunk[prevChunk.length - 1];
          const allButLast = prevChunk.slice(0, -1);
          return [
            ...allButLast,
            { ...lastMsg, text: lastMsg.text + chunk }
          ];
        });
      };

      (async () => {
        try {
          await sendMsgToAI(historyForApi, onChunk);
        } catch (error) {
          console.error("Error sending query:", error);
        } finally {
          setLoading(false);
        }
      })();
      
      return newHistory;
    });
  }, []); 

  const contextValue = {
    showSlide,
    setShowSlide,
    Mobile,
    setMobile,
    loading,
    setLoading,
    chatValue,
    setChatValue,
    message,
    setMessage,
    msgEnd,
    handleSend,
    handleKeyPress,
    handleQuery,
    language,
    setLanguage,
  };

  return (
    <ContextApp.Provider value={contextValue}>{children}</ContextApp.Provider>
  );
}

export default AppContext;
