export const sendMsgToAI = async (msg) => {
  const API_KEY = process.env.REACT_APP_GEMINI_KEY;

  // 1. Reverted to the non-streaming "generateContent" endpoint
  const API_URL = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash:generateContent?key=${API_KEY}`;

  const instructedMsg = `
  You are "AgroAssist," an expert agricultural assistant for farmers.
  Your job is to answer questions related to agriculture ONLY.

  --- YOUR PRIMARY RULE ---
  1. Read the user's question.
  2. Decide if it is related to agriculture (farming, crops, soil, fertilizer, weather for farming, farm pests, market prices for crops, etc.).
  3. IF THE QUESTION IS NOT about agriculture (e.g., politics, sports, history, who you are), you MUST ignore all other rules and respond ONLY with this exact sentence: "I am only programmed to answer queries regarding agriculture and that only alone."

  --- IF THE QUESTION IS ABOUT AGRICULTURE, FOLLOW THESE RULES ---
  1. **LANGUAGE RULE: You MUST respond in the exact same language as the User's question.** (e.g., If the user asks in Hindi, you must answer in Hindi).
  2. Give the most important, actionable answer in the very first sentence.
  3. Format your entire response using Markdown. Use paragraphs for explanations and bullet points for lists.
  4. Use simple, easy-to-understand language. Do NOT use scientific jargon.
  5. If the user's question can be answered in a short paragraph, do so (2-3 sentences max).
  6. If the user's question asks for a list of items (like fertilizers or steps), you MUST use a simple Markdown bulleted list (\`* item\`).
  7. Ensure proper line breaks and spacing for readability.

  User's question: ${msg}
  `;

  const requestOptions = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      contents: [
        {
          role: "user",
          parts: [{ text: instructedMsg.trim() }],
        },
      ],
    }),
  };

  try {
    const response = await fetch(API_URL, requestOptions);
    if (!response.ok) {
      const errorData = await response.json();
      console.error("Gemini API Error Response:", errorData);
      throw new Error(`API request failed with status ${response.status}: ${errorData?.error?.message || 'Unknown error'}`);
    }
    const data = await response.json();

    // 2. Return the full text response at once
    return data?.candidates?.[0]?.content?.parts?.[0]?.text || "No response text found.";
  } catch (error) {
    console.error("Gemini API call failed:", error);
    return `Error connecting to Gemini API: ${error.message}`;
  }
};
