// src/components/ChatContainer.js
import React, { useContext, useEffect, useRef } from "react";
import { ContextApp } from "../utils/Context";
import { LuPanelLeftOpen } from "react-icons/lu";
import { HiOutlineMenuAlt2 } from "react-icons/hi";
import { RiSendPlane2Fill } from "react-icons/ri";
import { FaMicrophone, FaCamera, FaTimesCircle } from "react-icons/fa"; // Added Camera and TimesCircle
import Chat from "./Chat";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

function ChatContainer() {
  const {
    showSlide, setShowSlide,
    setMobile, Mobile,
    chatValue, setChatValue,
    handleSend, handleKeyPress,
    // Get image state
    selectedImage, setSelectedImage,
    previewImage, setPreviewImage
  } = useContext(ContextApp);

  const {
    transcript, listening, resetTranscript, browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  // Ref for the hidden file input
  const fileInputRef = useRef(null);

  // Auto-send logic (remains the same)
  const wasListeningRef = useRef(false);
  useEffect(() => {
    if (wasListeningRef.current && !listening && transcript.trim()) {
      handleSend(transcript);
      resetTranscript();
    }
    wasListeningRef.current = listening;
  }, [listening, transcript, handleSend, resetTranscript]);

  // Sync transcript to input (remains the same)
  useEffect(() => {
    setChatValue(transcript);
  }, [transcript, setChatValue]);

  // handleListen (remains the same - defaults to English)
  const handleListen = () => {
    if (listening) {
      SpeechRecognition.stopListening();
    } else {
      resetTranscript();
      SpeechRecognition.startListening({ language: 'en-US' });
    }
  };

  // --- NEW: Handle Image Selection ---
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
        setSelectedImage(file);
        // Create a preview URL
        const reader = new FileReader();
        reader.onloadend = () => {
            setPreviewImage(reader.result);
        };
        reader.readAsDataURL(file);
    } else {
        // Handle invalid file type
        setSelectedImage(null);
        setPreviewImage(null);
        alert("Please select a valid image file (jpeg, png, webp, etc.).");
    }
    // Reset file input value so the same file can be selected again
    event.target.value = null;
  };

  // --- NEW: Trigger hidden file input ---
  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

   // --- NEW: Remove selected image ---
   const removeSelectedImage = () => {
       setSelectedImage(null);
       setPreviewImage(null);
   };

  return (
    <div
      className={
        showSlide
          ? "h-screen w-screen bg-gray-700 flex items-start justify-between flex-col p-2"
          : "h-screen w-full lg:w-[calc(100%-300px)] bg-gray-700 flex items-start justify-between flex-col p-2"
      }
    >
      {/* Sidebar toggles */}
      <span /* ... sidebar toggle ... */ > ... </span>
      <span /* ... mobile sidebar toggle ... */ > ... </span>

      <Chat />

      {/* Input area */}
      <div className=" w-full m-auto flex items-center justify-center flex-col gap-2 my-2">
        {/* --- Image Preview Area --- */}
        {previewImage && (
            <div className="relative mb-2">
                <img src={previewImage} alt="Selected preview" className="h-20 w-auto rounded border border-gray-500" />
                <button
                    onClick={removeSelectedImage}
                    className="absolute top-0 right-0 -mt-2 -mr-2 bg-gray-800 rounded-full p-1 text-white hover:bg-red-600 transition-colors"
                    title="Remove image"
                >
                    <FaTimesCircle size={18} />
                </button>
            </div>
        )}
        {/* --- End Image Preview Area --- */}

        <span className="flex gap-2 items-center justify-center bg-gray-600 rounded-lg shadow-md w-[90%] lg:w-2/5 xl:w-1/2">
          {/* Removed Language Dropdown */}

          {/* --- Hidden File Input --- */}
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleImageChange}
            accept="image/*" // Accept only image types
            className="hidden"
          />
          {/* --- End Hidden File Input --- */}

           {/* --- Camera Icon --- */}
           <FaCamera
             title="Upload image"
             className="text-white cursor-pointer text-xl ml-3 hover:text-gray-300" // Added margin-left
             onClick={triggerFileInput}
           />
           {/* --- End Camera Icon --- */}


          <input
            type="text"
            placeholder={listening ? "Listening..." : "Send a message (optional with image)"}
            className="h-full text-white bg-transparent px-2 py-4 w-full border-none outline-none text-base" // Adjusted padding
            value={chatValue}
            onChange={(e) => setChatValue(e.target.value)}
            onKeyUp={handleKeyPress}
          />

          {/* Mic Button */}
          <FaMicrophone /* ... mic button props ... */
             title={browserSupportsSpeechRecognition ? "Use voice (English only)" : "Voice not supported"}
             className={
               !browserSupportsSpeechRecognition
                 ? "text-gray-500 cursor-not-allowed text-xl mr-2"
                 : listening
                   ? "text-green-500 cursor-pointer text-xl mr-2"
                   : "text-white cursor-pointer text-xl mr-2"
             }
             onClick={browserSupportsSpeechRecognition ? handleListen : undefined}
          />

          {/* Send Button */}
          <RiSendPlane2Fill
            title="send message"
            className={
              // Enable send if text exists OR an image is selected
              (chatValue.length <= 0 && !selectedImage)
                ? "text-gray-400 cursor-not-allowed mr-3 text-xl" // Changed to not-allowed
                : "text-white cursor-pointer mr-3 text-3xl bg-green-500 p-1 rounded shadow-md "
            }
            // Disable onClick if nothing to send
            onClick={(chatValue.length > 0 || selectedImage) ? () => handleSend() : undefined}
          />
        </span>
      </div>
    </div>
  );
}

export default ChatContainer;
