import React, { useContext } from "react";
import ReactMarkdown from 'react-markdown';
import { ContextApp } from "../utils/Context";

// 1. IMPORT SKELETON LOADER
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css'; // Import skeleton CSS

function Chat() {
  // 2. GET LOADING STATE FROM CONTEXT
  const { message, msgEnd, loading } = useContext(ContextApp);

  return (
    <div className=" w-full h-[85%] flex items-center justify-center overflow-hidden overflow-y-auto px-2 py-1 scroll">
      <div className="w-full lg:w-4/5 flex flex-col h-full items-start justify-start">
        {message?.map((msg, i) => (
          <span
            key={i}
            className={
              msg.isBot
                ? "flex items-start justify-start gap-2 lg:gap-5 my-2 bg-gray-800/80 p-3 rounded-md w-full" // Bot style
                // 3. --- ALIGNMENT FIX ---
                // Removed "justify-center" from user's message
                : "flex items-start justify-start gap-2 lg:gap-5 my-2 p-3 w-full" // User style
            }
          >
            <img
              src={msg.isBot ? "/icon.png" : "/user.jpeg"}
              alt={msg.isBot ? "bot" : "user"}
              className="w-10 h-10 rounded object-cover flex-shrink-0"
            />
            {msg.isBot ? (
              <div className="text-white text-[15px] prose prose-invert max-w-none">
                <ReactMarkdown>{msg.text}</ReactMarkdown>
              </div>
            ) : (
              <p className="text-white text-[15px]">{msg?.text}</p>
            )}
          </span>
        ))}

        {/* 4. --- SKELETON LOADER --- */}
        {loading && (
          <span className="flex items-start justify-start gap-2 lg:gap-5 my-2 bg-gray-800/80 p-3 rounded-md w-full">
            <img
              src="/icon.png"
              alt="bot loading"
              className="w-10 h-10 rounded object-cover flex-shrink-0"
            />
            <SkeletonTheme baseColor="#374151" highlightColor="#4B5563">
              <div className="text-white text-[15px] w-full pt-1">
                <Skeleton count={2} /> 
              </div>
            </SkeletonTheme>
          </span>
        )}
        {/* --- END OF LOADER --- */}

        <div ref={msgEnd} />
      </div>
    </div>
  );
}

export default Chat;
