import React, { useContext, useEffect, useRef } from "react";
import { ContextApp } from "../utils/Context";
import { LuPanelLeftOpen } from "react-icons/lu";
import { HiOutlineMenuAlt2 } from "react-icons/hi";
import { RiSendPlane2Fill } from "react-icons/ri";
import { FaMicrophone } from "react-icons/fa";
import Chat from "./Chat";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

function ChatContainer() {
  const {
    showSlide,
    setShowSlide,
    setMobile,
    Mobile,
    chatValue,
    setChatValue,
    handleSend,
    handleKeyPress,
    // 1. Removed language and setLanguage from here as dropdown is gone
  } = useContext(ContextApp);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  // "auto-send" logic using transcript from mic
  const wasListeningRef = useRef(false);
  useEffect(() => {
    if (wasListeningRef.current && !listening && transcript.trim()) {
      handleSend(transcript); // Pass transcript directly
      resetTranscript();
    }
    wasListeningRef.current = listening;
  }, [listening, transcript, handleSend, resetTranscript]); // Added handleSend

  // Syncs the live transcript to the input box
  useEffect(() => {
    setChatValue(transcript);
  }, [transcript, setChatValue]);

  // handleListen function - now defaults to English
  const handleListen = () => {
    if (listening) {
      SpeechRecognition.stopListening();
    } else {
      resetTranscript();
      SpeechRecognition.startListening({
        language: 'en-US' // 2. Hardcoded to English (US)
      });
    }
  };

  return (
    <div
      className={
        showSlide
          ? "h-screen w-screen bg-gray-700 flex items-start justify-between flex-col p-2"
          : "h-screen w-full lg:w-[calc(100%-300px)] bg-gray-700 flex items-start justify-between flex-col p-2"
      }
    >
      {/* Sidebar toggles */}
      <span
        className="rounded px-3 py-[9px] hidden lg:flex items-center justify-center cursor-pointer text-white m-1 hover:bg-gray-600 duration-200"
        title="Open sidebar"
        onClick={() => setShowSlide(!showSlide)}
      >
        {showSlide && <LuPanelLeftOpen />}
      </span>
      <span
        className="rounded px-3 py-[9px] lg:hidden flex items-center justify-center cursor-pointer text-white mt-0 mb-3 border border-gray-600"
        title="Open sidebar"
        onClick={() => setMobile(!Mobile)}
      >
        <HiOutlineMenuAlt2 fontSize={20} />
      </span>

      {/* Chat messages area */}
      <Chat />

      {/* Input area */}
      <div className=" w-full  m-auto flex items-center justify-center flex-col gap-2 my-2">
        <span className="flex gap-2 items-center justify-center bg-gray-600 rounded-lg shadow-md w-[90%] lg:w-2/5 xl:w-1/2">

          {/* 3. --- REMOVED DROPDOWN --- */}
          {/* The <select> element was here */}
          {/* --- END REMOVAL --- */}

          <input
            type="text"
            placeholder={listening ? "Listening..." : "Send a message"}
            // Added padding-left to make space since dropdown is gone
            className="h-full text-white bg-transparent pl-3 pr-1 py-4 w-full border-none outline-none text-base"
            value={chatValue}
            onChange={(e) => setChatValue(e.target.value)}
            onKeyUp={handleKeyPress}
          />

          {/* Mic Button */}
          <FaMicrophone
            title={browserSupportsSpeechRecognition ? "Use voice (English only)" : "Voice not supported"} // Updated title
            className={
              !browserSupportsSpeechRecognition
                ? "text-gray-500 cursor-not-allowed text-xl mr-2"
                : listening
                  ? "text-green-500 cursor-pointer text-xl mr-2"
                  : "text-white cursor-pointer text-xl mr-2"
            }
            // Only allow clicking if supported
            onClick={browserSupportsSpeechRecognition ? handleListen : undefined}
          />

          {/* Send Button */}
          <RiSendPlane2Fill
            title="send message"
            className={
              chatValue.length <= 0
                ? "text-gray-400 cursor-auto mr-3 text-xl"
                : "text-white cursor-pointer mr-3 text-3xl bg-green-500 p-1 rounded shadow-md "
            }
            onClick={() => handleSend()} // Call handleSend without args for typed input
          />
        </span>
      </div>
    </div>
  );
}

export default ChatContainer;
