// src/utils/Context.js

import React, { createContext, useState, useRef, useEffect, useCallback } from "react";
import { sendMsgToAI } from "./Gemini"; // Ensure this is the streaming version

export const ContextApp = createContext();

function AppContext({ children }) {
  const [showSlide, setShowSlide] = useState(false);
  const [Mobile, setMobile] = useState(false);
  const [loading, setLoading] = useState(false); // Used briefly to indicate start
  const [chatValue, setChatValue] = useState("");
  const [message, setMessage] = useState([]);
  const msgEnd = useRef(null);
  const [language, setLanguage] = useState('en-US');

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [message]); // Only scroll when message updates

  // --- STREAMING handleSend ---
  const handleSend = useCallback(async (text) => {
    const promptText = text || chatValue;
    if (promptText.trim() === "") return;

    const userMessage = { text: promptText, isBot: false };
    const botMessageTemplate = { text: "", isBot: true }; // Empty template for streaming

    if (!text) { // Clear input only if text wasn't passed directly (e.g., from mic)
      setChatValue("");
    }

    setLoading(true); // Signal start

    // Use functional update to get the latest state
    setMessage((prev) => {
      const currentHistory = [...prev, userMessage]; // History to send to API
      const nextState = [...currentHistory, botMessageTemplate]; // State includes empty bot message

      // Define the callback for stream chunks
      const onChunk = (chunk) => {
        setMessage((prevChunk) => {
          const lastMsg = prevChunk[prevChunk.length - 1];
          // Ensure lastMsg exists and is a bot message before updating
          if (lastMsg && lastMsg.isBot) {
            const allButLast = prevChunk.slice(0, -1);
            return [
              ...allButLast,
              { ...lastMsg, text: lastMsg.text + chunk }
            ];
          }
          return prevChunk; // Return previous state if something is wrong
        });
      };

      // Call API within the updater to use correct history
      (async () => {
        try {
          await sendMsgToAI(currentHistory, onChunk); // Pass history *before* empty bot msg
        } catch (error) {
          console.error("Error during sendMsgToAI call:", error);
          // Update the last message with an error
           setMessage((prevErr) => {
             const lastMsg = prevErr[prevErr.length - 1];
             if (lastMsg && lastMsg.isBot) {
                const allButLast = prevErr.slice(0, -1);
                return [ ...allButLast, {...lastMsg, text: "Sorry, an error occurred."}];
             }
             return prevErr;
           });
        } finally {
          setLoading(false); // Signal end
        }
      })();

      return nextState; // Set state with user message + empty bot message
    });

  }, [chatValue]); // Dependency ensures handleSend updates if chatValue changes
  // --- END STREAMING handleSend ---

  const handleKeyPress = useCallback((e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend(); // Calls the streaming version
    }
  }, [handleSend]);

  // handleQuery needs the same streaming logic
  const handleQuery = useCallback(async (e) => {
    const text = e.target.innerText;
    if (!text) return;

    setLoading(true);
    setMessage((prev) => {
      const userMessage = { text, isBot: false };
      const botMessageTemplate = { text: "", isBot: true };
      const currentHistory = [...prev, userMessage];
      const nextState = [...currentHistory, botMessageTemplate];

      const onChunk = (chunk) => {
         setMessage((prevChunk) => {
          const lastMsg = prevChunk[prevChunk.length - 1];
          if (lastMsg && lastMsg.isBot) {
            const allButLast = prevChunk.slice(0, -1);
            return [ ...allButLast, { ...lastMsg, text: lastMsg.text + chunk }];
          }
           return prevChunk;
        });
      };

      (async () => {
        try {
          await sendMsgToAI(currentHistory, onChunk);
        } catch (error) {
          console.error("Error sending query:", error);
           setMessage((prevErr) => {
             const lastMsg = prevErr[prevErr.length - 1];
             if (lastMsg && lastMsg.isBot) {
                const allButLast = prevErr.slice(0, -1);
                return [ ...allButLast, {...lastMsg, text: "Sorry, an error occurred."}];
             }
             return prevErr;
           });
        } finally {
          setLoading(false);
        }
      })();

      return nextState;
    });
  }, []);

  const contextValue = {
    showSlide,
    setShowSlide,
    Mobile,
    setMobile,
    loading,
    setLoading,
    chatValue,
    setChatValue,
    message,
    setMessage,
    msgEnd,
    handleSend,
    handleKeyPress,
    handleQuery,
    language,
    setLanguage,
  };

  return (
    <ContextApp.Provider value={contextValue}>{children}</ContextApp.Provider>
  );
}

export default AppContext;
